import React, { useState, useEffect } from 'react'
import Navbar from 'react-bootstrap/Navbar';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

const Home = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [photos, setPhotos] = useState([]);
  const [page, setPage] = useState(1);

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleSearchSubmit = (e) => {
    e.preventDefault();
    // Fetch photos from the Flickr API based on the search term
    fetchPhotos(searchTerm);
  };

  const fetchPhotos = (query) => {
    const apiKey = '47faaf86d81bc536bdd4724183fbaff7';
    const apiUrl = `https://www.flickr.com/services/rest/?method=flickr.photos.search&api_key=${apiKey}&text=${query}&format=json&nojsoncallback=1&page=${page}&per_page=10`;

    fetch(apiUrl)
    .then((response) => response.json())
    .then((data) => {
      const newPhotos = data.photos.photo;
      setPhotos((prevPhotos) => [...prevPhotos, ...newPhotos]);
      setPage(page + 1);
    })
    .catch((error) => console.error(error));
};


const handleScroll = () => {
  if (
    window.innerHeight + document.documentElement.scrollTop ===
    document.documentElement.offsetHeight
  ) {
    fetchPhotos(searchTerm);
  }
};


useEffect(() => {
  window.addEventListener('scroll', handleScroll);
  return () => {
    window.removeEventListener('scroll', handleScroll);
  };
}, []);


  return (
  <>
          <Navbar className="bg-body-tertiary justify-content-between">
          <Navbar.Brand >ORI-PHOTOS</Navbar.Brand>
      <Form  onSubmit={handleSearchSubmit}>
        <Row>
          <Col xs="auto">
            <Form.Control
              type="text"
              placeholder="Search"
              className=" mr-sm-2"
              value={searchTerm}
              onChange={handleSearchChange}
            />
          </Col>
          <Col xs="auto">
            <Button type="submit">Submit</Button>
          </Col>
        </Row>
      </Form>
    </Navbar>



    <div className="photo-grid">
        {photos.map((photo) => (
          // Render each photo with a preview modal
          // Implement a modal component for the preview
          // You can use libraries like react-bootstrap for modals
          <div key={photo.id} className="photo-card">
            <img
              src={`https://farm${photo.farm}.staticflickr.com/${photo.server}/${photo.id}_${photo.secret}.jpg`}
              alt={photo.title}
            />
            {/* Implement a click handler to open the preview modal */}
          </div>
        ))}
      </div>

  </>
  );
};

export default Home
